import React, { useState, useEffect } from 'react';
import { Dropdown, NavDropdown } from 'react-bootstrap';
import fetchMenu from './fetchMenu';

const DropdownMenu = ({ menuSlug }) => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    const getMenuItems = async () => {
      const items = await fetchMenu(menuSlug);
      setMenuItems(items);
    };
    getMenuItems();
  }, [menuSlug]);

  const renderMenuItems = (items) => {
    return items.map((item) => (
      <React.Fragment key={item.ID}>
        {item.child_items ? (
          <NavDropdown title={item.title} id={`submenu-${item.ID}`}>
            {renderMenuItems(item.child_items)}
          </NavDropdown>
        ) : (
          <Dropdown.Item href={item.url}>{item.title}</Dropdown.Item>
        )}
      </React.Fragment>
    ));
  };

  return (
    <Dropdown>
      <Dropdown.Toggle variant="primary" id="dropdown-basic">
        Menu
      </Dropdown.Toggle>
      <Dropdown.Menu>{renderMenuItems(menuItems)}</Dropdown.Menu>
    </Dropdown>
  );
};

export default DropdownMenu;
