import React from "react";
const TripleCardSection = ({ title, text, cards }) => {
  return (
    <div className="container pt-5 pb-5">
   {title && (
        <div className="row">
          <h2 className="text-center">{title}</h2>
        </div>
      )}
      {text && (
        <div className="row">
          <p className="text-center">{text}</p>
        </div>
      )}
           <div className="row justify-content-center cardsrowtrip gap-4 pt-5">
        {cards?.length > 0 &&
          Object.entries(cards).map(([key, card]) => (
            <div key={key} className="singleCardtriple col-12 col-md-3 col-lg-3 mb-4 align-items-center" style={card?.backgroundImage?.node?.mediaItemUrl ? {backgroundImage:`url(${card.backgroundImage.node.mediaItemUrl})`}:{}}
            >
              {card?.title && <h3 className="cardsTitleTrip pb-4">{card.title}</h3>}
              {card?.text && <p className="cardText">{card.text}</p>}
              {card?.buttonText && card?.buttonUrl && (
                <a href={card.buttonUrl} className="cardButton">
                  {card.buttonText} &rarr;
                </a>
              )}
            </div>
          ))}
      </div>
    </div>

  );
};
export default TripleCardSection;
