import React from "react";
import "../styles/Tabs.css";

const Tabs = ({ title, text, tabs, sectioncounter }) => {
  //   console.log("Tabs Data:", tabs);

  return (
    <div className="container pt-5 pb-5">
      {title && <h2 className="text-center">{title}</h2>}
      {text && (
        <div
          className="text-center"
          dangerouslySetInnerHTML={{ __html: text }}
        />
      )}

      {/* Main Tabs */}
      <ul className="nav nav-pills mb-3" role="tablist">
        {tabs?.map((tab, index) => (
          <li className="nav-item" role="presentation" key={index}>
            <button
              className={`nav-link ${index === 0 ? "active" : ""}`}
              data-bs-toggle="pill"
              data-bs-target={`#tab-${sectioncounter}-${index}`}
              type="button"
              role="tab"
              aria-selected={index === 0 ? "true" : "false"}
            >
              {tab.generalTabTitle}
            </button>
          </li>
        ))}
      </ul>

      <div className="tab-content">
        {tabs?.map((tab, index) => (
          <div
            key={index}
            className={`tab-pane fade ${index === 0 ? "show active" : ""}`}
            id={`tab-${sectioncounter}-${index}`}
            role="tabpanel"
          >
            {/* Internal Tabs */}
            {tab?.internalTab && tab.internalTab.length > 0 && (
              <>
                <ul className="nav nav-pills mb-3" role="tablist">
                  {tab.internalTab.map((intTab, intIndex) => (
                    <li className="nav-item" role="presentation" key={intIndex}>
                      <button
                        className={`nav-link ${intIndex === 0 ? "active" : ""}`}
                        data-bs-toggle="pill"
                        data-bs-target={`#internal-tab-${sectioncounter}-${index}-${intIndex}`}
                        type="button"
                        role="tab"
                        aria-selected={intIndex === 0 ? "true" : "false"}
                      >
                        {intTab.title}
                      </button>
                    </li>
                  ))}
                </ul>

                {/* Internal Tab Content */}
                <div className="tab-content">
                  {tab.internalTab.map((intTab, intIndex) => (
                    <div
                      key={intIndex}
                      className={`tab-pane fade ${
                        intIndex === 0 ? "show active" : ""
                      }`}
                      id={`internal-tab-${sectioncounter}-${index}-${intIndex}`}
                      role="tabpanel"
                    >
                      {/* Cards Section */}
                      <div className="row">
  {intTab.tabCard.map((card, cardIndex) => (
    <div key={cardIndex} className="col-md-3">
      <div
        className={`card ${card.infoCard ? "infocard" : ""}`}
        style={{
          backgroundColor: card.backgroundColor || "white",
          color: card.textColor || "black",
        }}
      >
        <div className="card-body" style={{ color: card.textColor || "black" }}>
          <h5 className="card-title text-center">
            {card?.title ? card.title : "\u00A0"}
          </h5>
          <ul style={{ color: card.textColor || "black" }}>
            {card.row.map((rowItem, rowIndex) => (
              <li key={rowIndex}>{rowItem.text}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  ))}
</div>


                      <a href={intTab.button.url} className="btn btn-primary">
                        {intTab.button.text}
                      </a>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tabs;
