import React from "react";
import { useQuery } from "@apollo/client";
import { GET_HEADER_MENU } from "../apollo/queries"; // Assuming the query is located in the queries file
import "./styles/Menu.css";

const Menu = () => {
  const { loading, error, data } = useQuery(GET_HEADER_MENU);

  // Show loading state while fetching data
  if (loading) return <p>Loading...</p>;

  // Show error state if there is an error
  if (error) return <p>Error loading navigation: {error.message}</p>;

  // Extract the menu items from the query result
  const menuItems = data.menuItems?.edges || [];

  // Recursive function to render nested menu items
  const renderMenuItems = (items) => {
    return items.map(({ node }) => {
      // Check if childItems exist and are not empty
      const hasChildren = node.childItems && node.childItems.nodes && Array.isArray(node.childItems.nodes) && node.childItems.nodes.length > 0;
      
      // Log to help debug the structure of `node` and `node.childItems`
      console.log('Node:', node);
      console.log('Child Items:', node.childItems);

      return (
        <li className={`nav-item ${hasChildren ? "dropdown" : ""}`} key={node.id}>
          <a
            className={`nav-link ${hasChildren ? "dropdown-toggle" : ""}`}
            href={node.uri}
            target={node.target || "_self"}
            role={hasChildren ? "button" : undefined}
            data-bs-toggle={hasChildren ? "dropdown" : undefined}
            aria-expanded={hasChildren ? "false" : undefined}
          >
            {node.label}
          </a>
          {hasChildren && (
            <ul className="dropdown-menu">
              {renderMenuItems(node.childItems.nodes)} {/* Recursive call */}
            </ul>
          )}
        </li>
      );
    });
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light">
      <ul className="navbar-nav">{renderMenuItems(menuItems)}</ul>
    </nav>
  );
};

export default Menu;
