import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Nav, NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost/fundedprime/wp-json/menus/v1/menus/header_menu')
      .then(response => {
        console.log('API Response:', response.data); // Log API data
        const menuTree = buildMenuTree(response.data);
        console.log('Menu Tree:', menuTree); // Log the built menu tree
        setMenuItems(menuTree);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching menu:', error);
        setLoading(false);
      });
  }, []);

  const buildMenuTree = (items) => {
    const menuMap = {};
    const menuTree = [];

    // Map items by ID and initialize children
    items.forEach(item => {
      menuMap[item.id] = { ...item, children: [] };
    });

    // Build tree structure
    items.forEach(item => {
      if (item.menu_item_parent === 0) {
        menuTree.push(menuMap[item.id]); // Top-level item
      } else {
        const parent = menuMap[item.menu_item_parent];
        if (parent) {
          parent.children.push(menuMap[item.id]); // Add to parent's children
        }
      }
    });

    return menuTree;
  };

  const renderMenuItems = (items) => {
    console.log('Rendering Items:', items); // Debug render items

    return items.map(item => {
      if (item.children && item.children.length > 0) {
        console.log('Rendering Parent with Children:', item);
        return (
          <NavDropdown key={item.id} title={item.title} id={`navbarDropdown${item.id}`}>
            {renderMenuItems(item.children)}
          </NavDropdown>
        );
      }

      console.log('Rendering Single Item:', item);
      return (
        <Nav.Item key={item.id}>
          <Nav.Link href={item.url}>{item.title}</Nav.Link>
        </Nav.Item>
      );
    });
  };

  if (loading) {
    return <p>Loading menu...</p>;
  }

  return (
    <Nav>
      {renderMenuItems(menuItems)}
    </Nav>
  );
};

export default Menu;
