import React from "react";

const TextLeftImageRight = ({ title, text, imageUrl, imgPosition, bgColor }) => (
  <div className="section pt-5 pb-5 {bgColor}" style={{ backgroundColor: bgColor || "inherit" }}>
    <div className="container">
      <div className="row" style={{ backgroundColor: bgColor || "inherit" }}>
        <div className="textcolumn col-7">
          {/* Conditionally render title if it's not empty */}
          {title && <h2>{title}</h2>}
          
          {/* Conditionally render text if it's not empty */}
          {text && <div className="ir_text" dangerouslySetInnerHTML={{ __html: text }} />}
        </div>

        <div className="imagecolumn col-5">
          {/* Conditionally render image if imageUrl is not empty */}
          {imageUrl && <img className="imageRight" src={imageUrl} alt="Section visual" />}
        </div>
      </div>
    </div>
  </div>
);

export default TextLeftImageRight;
