import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Nav, NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost/fundedprime/wp-json/menus/v1/menus/header_menu')
      .then(response => {
        console.log('Fetched menu data:', response.data); // Check the data structure
        setMenuItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching menu:', error);
        setLoading(false);
      });
  }, []);

  // Guard clause if menuItems is not an array or empty
  if (!Array.isArray(menuItems) || menuItems.length === 0) {
    return <p>No menu items available.</p>;
  }

  // Function to group menu items by their parent
  const groupItemsByParent = (items) => {
    const grouped = {};
    items.forEach(item => {
      const parentId = item.menu_item_parent || 0;  // Default to 0 for parent items
      if (!grouped[parentId]) {
        grouped[parentId] = [];
      }
      grouped[parentId].push(item);
    });
    return grouped;
  };

  const renderMenuItem = (item, groupedItems) => {
    const children = groupedItems[item.id] || []; // Get child items if they exist
  
    // If the item has children, render it as a dropdown
    if (children.length > 0) {
      return (
        <NavDropdown key={item.id} title={item.title} id={`navbarDropdown${item.id}`}>
          {children.map((child) => (
            <NavDropdown.Item key={`child-${child.id}`} href={child.url}>
              {child.title}
            </NavDropdown.Item>
          ))}
        </NavDropdown>
      );
    }
  
    // Otherwise, render it as a simple nav link
    return (
      <Nav.Item key={`parent-${item.id}`}>
        <Nav.Link href={item.url}>{item.title}</Nav.Link>
      </Nav.Item>
    );
  };

  const groupedItems = groupItemsByParent(menuItems);

  // Find the top-level items (those with parent ID of 0)
  const topLevelItems = groupedItems[0] || [];

  return (
    <Nav>
      {topLevelItems.map((item) => renderMenuItem(item, groupedItems))}
    </Nav>
  );
};

export default Menu;
