import React from "react";
import { useParams } from "react-router-dom";
import "../components/styles/style.css";
import { useQuery } from "@apollo/client";
import { GET_SECTION } from "../apollo/queries";
import VideoHeader from "../components/template-parts/VideoHeader";

const DefaultTemplate = () => {
  const { slug } = useParams();
  const currentSlug = slug || "404";

  const { loading, error, data } = useQuery(GET_SECTION, {
    variables: { slug: currentSlug },
  });

  if (loading) {
    return <div>Loading...</div>;
  }
  if (error) {
    return <div>Error loading data: {error.message}</div>;
  }

  const sections = data.pageBy.sections;
  console.log(sections);

  return (
    <div>
      <h2>Test</h2>
      <p>This is the default page template.</p>
      {sections.section.map((section, index) => {
        let content;

        // Switch based on the first value of `section.selectSection`
        const selectedValue = section.selectSection?.[0];

        switch (selectedValue) {
          case "video_header":
            content =         <VideoHeader buttonsa={topVideoSection.buttons} text={topVideoSection.text} videourl={topVideoSection.video.node.mediaItemUrl}/>            ;
            break;
          case "text_img_columns":
            content = (
              <div>
                <h3>{section.videoHeader?.header || "No Header"}</h3>
                <video controls src={section.videoHeader?.videoUrl || ""} />
              </div>
            );
            break;

          default:
            content = <p>Unknown section type or no selectSection value</p>;
        }

        return (
          <div key={index} style={{ marginBottom: "20px" }}>
            <h2>Section {index + 1}</h2>
            {content}
          </div>
        );
      })}
    </div>
  );
};

export default DefaultTemplate;
