import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Nav, NavDropdown } from 'react-bootstrap';
import './styles/Menu.css';  // Import custom CSS for positioning

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the WordPress menu by slug (replace with your actual slug)
    axios.get('http://localhost/fundedprime/wp-json/menus/v1/menus/header_menu')
      .then(response => {
        setMenuItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching menu:', error);
        setLoading(false);
      });
  }, []);
  const renderMenuItem = (item) => {
console.log(item);
// If the menu item has children, render it as a dropdown
    if (item.child_items && item.child_items.length > 0) {
      return (
        <NavDropdown key={item.id} title={item.title} id={`navbarDropdown${item.id}`}>
          {item.child_items.map((child) => (
            <NavDropdown.Item key={child.id} href={child.url}>
              {child.title}
            </NavDropdown.Item>
          ))}
        </NavDropdown>
      );
    }

    // Otherwise, render it as a simple nav link
    return (
      <Nav.Item key={item.id}>
        <Nav.Link href={item.url}>{item.title}</Nav.Link>
      </Nav.Item>
    );
  };

  if (loading) {
    return <p>Loading menu...</p>;
  }

  return (
    <Nav>
      {menuItems.map(renderMenuItem)}
    </Nav>
  );
};

export default Menu;
