import React from "react";
import "./styles/Menu.css";
import {fetchMenu} from "../api";
import "./styles/style.css";
import { useQuery } from "@apollo/client";
import { GET_HEADER_MENU } from "../apollo/queries";
const Menu = () => {
    const { loading, error, data } = useQuery(GET_HEADER_MENU);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error loading navigation: {error.message}</p>;
  const menuItems = data.menuItems?.edges || [];

console.log(menuItems);
// const [menuItems, setMenuItems] = useState([]);
  
//   useEffect(() => {
//     const fetchData = async () => {
//       const fetchedMenu = await fetchMenu();
//       setMenuItems(fetchedMenu);
// // console.log(fetchedMenu);
//     };
//     fetchData();
//   }, []);
  const renderMenuItems = (items) => {
    return menuItems.map((item) => {
      if (item.node.children && item.node.children.length > 0) {
        return (
          <li className="nav-item dropdown" key={item.node.id}>
            <a
              className="nav-link dropdown-toggle"
              href="#"
              id={`nav-dropdown-${item.node.id}`}
              role="button"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              {item.node.title}
            </a>
            <ul
              className="dropdown-menu"
              aria-labelledby={`nav-dropdown-${item.node.id}`}
            >
              {renderMenuItems(item.node.children)}
            </ul>
          </li>
        );
      }

      return (
        <li className="nav-item" key={item.id}>
          <a className="nav-link" href={item.slug}>
            {item.node.title}
          </a>
        </li>
      );
    });
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light">
      <ul className="navbar-nav">{renderMenuItems(menuItems)}</ul>
    </nav>
  );
};

export default Menu;
