import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Nav, NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost/fundedprime/wp-json/menus/v1/menus/header_menu')
      .then(response => {
        console.log('Fetched menu data:', response.data); // Check for undefined IDs or parents
        setMenuItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching menu:', error);
        setLoading(false);
      });
  }, []);

  const groupItemsByParent = (items) => {
    const grouped = {};
    items.forEach(item => {
      const parentId = item.menu_item_parent || 0; // Default to 0 for top-level items
      if (!grouped[parentId]) {
        grouped[parentId] = [];
      }
      grouped[parentId].push(item);
    });
    return grouped;
  };

  const renderMenuItem = (item, groupedItems) => {
    const children = groupedItems[item.id] || [];
    const uniqueKey = item.id || `generated-${item.title}-${item.url}`; // Generate a fallback key

    if (children.length > 0) {
      return (
        <NavDropdown key={uniqueKey} title={item.title} id={`navbarDropdown${uniqueKey}`}>
          {children.map((child) => (
            <NavDropdown.Item key={child.id || `child-${child.title}-${child.url}`} href={child.url}>
              {child.title}
            </NavDropdown.Item>
          ))}
        </NavDropdown>
      );
    }

    return (
      <Nav.Item key={uniqueKey}>
        <Nav.Link href={item.url}>{item.title}</Nav.Link>
      </Nav.Item>
    );
  };

  if (loading) {
    return <p>Loading menu...</p>;
  }

  const validMenuItems = menuItems.filter(item => item.id !== undefined); // Filter invalid items
  const groupedItems = groupItemsByParent(validMenuItems);
  const topLevelItems = groupedItems[0] || [];

  return (
    <Nav>
      {topLevelItems.map(item => renderMenuItem(item, groupedItems))}
    </Nav>
  );
};

export default Menu;
