// src/templates/Page.js
import React, { useEffect, useState } from 'react';
import { fetchPageData } from '../api';

const Page = ({ slug }) => {
  const [page, setPage] = useState(null);

  useEffect(() => {
    const getPageData = async () => {
      const data = await fetchPageData(slug); // Fetch page data by slug
      setPage(data);
    };
    getPageData();
  }, [slug]);

  if (!page) {
    return <div>Loading...</div>;
  }

  // Dynamically load the template based on the fetched template name
  let TemplateComponent;
  if (page.template === 'home-template.php') {
    TemplateComponent = () => <div>Home Template Content</div>; // This is just an example
  } else if (page.template === 'about-template.php') {
    TemplateComponent = () => <div>About Template Content</div>; // Another example
  } else {
    TemplateComponent = () => <div>{page.content.rendered}</div>;
  }

  return (
    <div>
      <h1>{page.title.rendered}</h1>
      <TemplateComponent />
    </div>
  );
};

export default Page;
