import React from "react";
import { useQuery } from "@apollo/client";
import { gql } from "@apollo/client";
import "./styles/Menu.css";

// Define the GraphQL query for fetching menu items
const GET_HEADER_MENU = gql`
  query GetHeaderMenu {
    menuItems {
      edges {
        node {
          id
          title
          url
          parentId
          childItems {
            edges {
              node {
                id
                title
                url
              }
            }
          }
        }
      }
    }
  }
`;

const Menu = () => {
  const { loading, error, data } = useQuery(GET_HEADER_MENU);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading navigation: {error.message}</p>;

  // Extract menu items from the data
  const menuItems = data.menuItems?.edges || [];

  // Recursive function to render nested menu items
  const renderMenuItems = (items) => {
    return items.map(({ node }) => {
      const hasChildren = node.childItems?.edges?.length > 0;

      return (
        <li className={`nav-item ${hasChildren ? "dropdown" : ""}`} key={node.id}>
          <a
            className={`nav-link ${hasChildren ? "dropdown-toggle" : ""}`}
            href={node.url}
            role={hasChildren ? "button" : undefined}
            data-bs-toggle={hasChildren ? "dropdown" : undefined}
            aria-expanded={hasChildren ? "false" : undefined}
          >
            {node.title}
          </a>
          {hasChildren && (
            <ul className="dropdown-menu">
              {renderMenuItems(node.childItems.edges)}
            </ul>
          )}
        </li>
      );
    });
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light">
      <ul className="navbar-nav">{renderMenuItems(menuItems)}</ul>
    </nav>
  );
};

export default Menu;
