import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {Navbar, Container, Nav, NavDropdown} from 'react-bootstrap';

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Replace {menu-slug} with your actual menu slug
    axios.get('http://localhost/fundedprime/wp-json/menus/v1/menus/header_menu')
      .then(response => {
        setMenuItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching menu:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading menu...</p>;
  }

  return (

              // <NavDropdown title="Dropdown" id="collasible-nav-dropdown">  
              //   <NavDropdown.Item href="#action/3.1">Item 1</NavDropdown.Item>  
              //   <NavDropdown.Item href="#action/3.2">Item 2</NavDropdown.Item>  
              //   <NavDropdown.Item href="#action/3.3">Item 3</NavDropdown.Item>  
              //   <NavDropdown.Divider />  
              //   <NavDropdown.Item href="#action/3.4">Separated Item</NavDropdown.Item>  
              // </NavDropdown>  
    <Nav>
        {menuItems.map(item => (
              <Nav.Link key={item.id} href={item.url}>{item.title}</Nav.Link>  
        ))}
    </Nav>
    
  );
};

export default Menu;
